var EIQAPIcalls = Class.create();
EIQAPIcalls.prototype = {
    initialize: function() {},

    observable_lookup_call: function(observable_value) {
        var eiq_util = new x_1088979_eclectic.EIQConfigurationUtils();
        var config = eiq_util.getConfigurationByIntItemConfigId("39843d1e2f33211055a656e62799b6d9");

        try {
            var r = new sn_ws.RESTMessageV2('x_1088979_eclectic.Lookup EIQ Observable', 'Lookup EIQ Observable');
            r.setStringParameterNoEscape('observable_value', observable_value);
            r.setStringParameterNoEscape('base_url', config["base_url"]["value"]);
			r.setQueryParameter('query_key', 'filter[value]');
			r.setRequestHeader("Authorization", "Bearer " + config["api_token"]["value"]);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
			gs.info("Completed observable request with status code" + httpStatus);
			return responseBody;
        } catch (ex) {
            var message = ex.message;
			return message;
        }

    },

    type: 'EIQAPIcalls'
};
